%Leer la imagen 1h1=imread('C:\Users\Francisco\Pictures\hibrid1.png');%Crear filtro gaussiano 30x30 sigma 5fg=fspecial('gaussian',[30 30], 5);%Crear imagen de baja frecuencia lf=imfilter(h1,fg);%Obtener imagen de altas frecuencias para imagen 1hf=h1-lf;%la imagen hf es tratada con Gimp para mejorar su brillo y contraste%Leer imagen2h2=imread('C:\Users\Francisco\Pictures\hibrid2.png');%Crear imagen de bajas frecuencias para imagen 2lf=imfilter(h2,fg);%Construir la imagen finalhi=hf+lf;%Mostar los resultadosI0 = hi;I1 = impyramid(I0, 'reduce');I2 = impyramid(I1, 'reduce');I3 = impyramid(I2, 'reduce');imshow(I0)figure, imshow(I1)figure, imshow(I2)figure, imshow(I3)